package com.syntax.reviewclass02;

public class ShortHAndOperatorsDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=10; 
		num=num+1; 
		
		System.out.println(num);//11
		num++; 
		System.out.println(num);//11+1
		num+=10;  //num=num+10
		System.out.println(num);//12+10
		num*=100; 
		System.out.println(num);//22*100
		num=10; 
		++num; 
		System.out.println(num);
		num++;
		System.out.println(num);
	}

}
