package com.syntax.reviewclass04;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=1; 
		
		while (i<=5) {
		System.out.println("Batch 12 is great");
		i++; 
		System.out.println("*********");
		
		int num=1; 
		while(num<=4) {
			System.out.println("Batch 12 is great" +num );
			num++; 
			
		}
		}
	}

}
